import React from "react";
import "./ChessBoardStyle.css";

const ChessBoard = () => {
    const renderCell = (row, col) => {
        const isBlack = (row + col) % 2 === 1;
        const notation = getCellNotation(row, col);
        const cellColor = isBlack ? "black" : "white";

        return (
            <div className={`cell ${cellColor}`} key={`${row}-${col}`}>
                <div className="notation">{notation}</div>
            </div>
        );
    };

    const getCellNotation = (row, col) => {
        const letters = ["A", "B", "C", "D", "E", "F", "G", "H"];
        const number = 8 - row;
        const letter = letters[col];
        return `${letter}${number}`;
    };

    const renderRow = (row) => {
        const cells = [];
        for (let col = 0; col < 8; col++) {
            cells.push(renderCell(row, col));
        }
        return <div className="row" key={row}>{cells}</div>;
    };

    const renderChessboard = () => {
        const rows = [];
        for (let row = 0; row < 8; row++) {
            rows.push(renderRow(row));
        }
        return rows;
    };

    return <div className="chessboard">{renderChessboard()}</div>;
};

export default ChessBoard;
