import React, { useState } from "react";

const jobs = [
    { id: 1, name: "Developer" },
    { id: 2, name: "Designer" },
    { id: 3, name: "Writer" },
    { id: 4, name: "Teacher" },
    { id: 5, name: "Engineer" },
];

const JobMenu = () => {
    const [selectedJob, setSelectedJob] = useState(null);

    const handleJobChange = (event) => {
        setSelectedJob(event.target.value);
    };

    return (
        <div>
            <h2>Job Menu</h2>
            <div>
                <h3>Select a profession:</h3>
                {jobs.map((job) => (
                    <label key={job.id}>
                        <input
                            type="radio"
                            value={job.name}
                            checked={selectedJob === job.name}
                            onChange={handleJobChange}
                        />
                        {job.name}
                    </label>
                ))}
            </div>
            {selectedJob && <MenuOptions selectedJob={selectedJob} />}
        </div>
    );
};

const MenuOptions = ({ selectedJob }) => {
    // Define menu options for each profession
    const menuOptionsByJob = {
        Developer: [
            "Tutorials",
            "Code Samples",
            "Frameworks",
            "Tools",
            "Blogs",
            "Forums",
            "Courses",
        ],
        Designer: [
            "Inspiration",
            "Color Palettes",
            "Typography",
            "UI Kits",
            "Design Tools",
            "Communities",
            "Courses",
        ],
        Writer: [
            "Writing Tips",
            "Grammar Resources",
            "Publishing Platforms",
            "Writing Prompts",
            "Literary Magazines",
            "Writing Communities",
            "Courses",
        ],
        Teacher: [
            "Lesson Plans",
            "Teaching Resources",
            "Educational Websites",
            "Classroom Management",
            "Professional Development",
            "Teaching Communities",
            "Courses",
        ],
        Engineer: [
            "Engineering Principles",
            "Code Repositories",
            "Technical Documentation",
            "Hardware Resources",
            "Engineering Forums",
            "Engineering Communities",
            "Courses",
        ],
    };

    const options = menuOptionsByJob[selectedJob];

    return (
        <div>
            <h3>Menu Options for {selectedJob}</h3>
            <ul style={{listStyleType: "none"}}>
                {options.map((option, index) => (
                    <li key={index}>{option}</li>
                ))}
            </ul>
        </div>
    );
};

export default JobMenu;
