import React, {useState} from 'react';

const EmailForm = () => {
    const [email, setEmail] = useState("");
    const [emailDirty, setEmailDirty] = useState(false);
    const [emailError, setEmailError] = useState("Incorrectly entered email");

    const emailHandler = (e) => {
        setEmail(e.target.value);
        const validate = String(e.target.value)
            .toLowerCase()
            .match(
                /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|.(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/
            );

        if(!validate){
            setEmailError("Incorrectly email");
        }
    }

    const blurHandler = (e) => {
        setEmailDirty(true);
    }

    return (
        <div>
            <form>
                {(emailDirty && emailError) && <div style={{color: "red"}}>{emailError}</div>}
                <h1>Send your Email</h1>
                <input value={email} onBlur={e => blurHandler(e)} type="email"/><br/>
                <button type="submit">Submit</button><br/>
            </form>
        </div>
    );
};

export default EmailForm;
