import React, {useEffect, useState} from 'react';
import './ToDoItemsStyle.css';

const ToDoItems = ({list}) => {
    const [sortedList, setSortedList] = useState(list);
    const [sortConfig, setSortConfig] = useState("")

    useEffect(() => {
        const sorted = [...sortedList].sort((a, b) => {
            const valueA = a[sortConfig];
            const valueB = b[sortConfig];
            if (valueA < valueB) {
                return sortConfig.direction === 'asc' ? -1 : 1;
            }
            if (valueA > valueB) {
                return sortConfig.direction === 'asc' ? 1 : -1;
            }
            return 0;
        });

        setSortedList(sorted);
    }, [sortConfig]);

    return (
        <div className={"todo-items"}>
            <select onChange={e => setSortConfig(e.target.value)}>
                <option value="done">Выполненные задачи</option>
                <option value="undone">Невыполненные задачи</option>
            </select>
            {list?.length > 0 ? (
                list.map((toDoItem, index) => (
                    <div className="task-item" key={index}>
                        <input className={"task-stage"}
                            type="checkbox"
                        />
                        <span>{toDoItem}</span>
                    </div>
                ))
            ):(
                <div className="empty">
                    <p>No task found</p>
                </div>
            )}
        </div>
    );
};

export default ToDoItems;
