import React, {useEffect, useState} from 'react';
import './ToDoItemsStyle.css';

const ToDoItems = ({list}) => {
    const [sortedList, setSortedList] = useState(list);
    const [sortConfig, setSortConfig] = useState(false)

    useEffect(() => {
        const sorted = [...sortedList].filter((element) => {
            if (element.stage === sortConfig) return true;
            return false;
        });
        console.log(list)
        setSortedList(sorted);
    }, [sortConfig, list]);

    return (
        <div className={"todo-items"}>
            <select onChange={e => setSortConfig(e.target.value)}>
                <option value={true}>Выполненные задачи</option>
                <option value={false} selected>Невыполненные задачи</option>
            </select>
            {list?.length > 0 ? (
                sortedList.map((toDoItem) => (
                    <div className="task-item" key={toDoItem.task}>
                        <input className={"task-stage"}
                            type="checkbox"
                        />
                        <span>{toDoItem.task}</span>
                    </div>
                ))
            ):(
                <div className="empty">
                    <p>No task found</p>
                </div>
            )}
        </div>
    );
};

export default ToDoItems;
