import React, {useEffect, useState} from 'react';
import './ToDoItemsStyle.css';

const ToDoItems = ({item}) => {
    const [listToDoItems, setListTodoItems] = useState([]);
    const [flag, setFlag] = useState(true);

    useEffect(() => {
        if(flag){
            setListTodoItems();
            setFlag(false);
        }
        if(item !== undefined){
            setListTodoItems([...listToDoItems, item]);
        }
        console.log(listToDoItems);
    }, [item]);

    return (
        <div className={"todo-items"}>
            {listToDoItems?.length > 0 ? (
                listToDoItems.map((toDoItem, index) => (
                    <div className="task-item" key={index}>
                        <input className={"task-stage"}
                            type="checkbox"
                        />
                        <span>{toDoItem}</span>
                    </div>
                ))
            ):(
                <div></div>
            )}
        </div>
    );
};

export default ToDoItems;
