import React, {useEffect, useState} from 'react';

const ToDoForm = ({setItems}) => {
    const [task, setTask] = useState("");
    const [taskDirty, setTaskDirty] = useState(false);
    const [taskError, setTaskError] = useState("Поле task пустое");
    const [formValid, setFormValid] = useState(false);



    useEffect(() => {
        if(taskError){
            setFormValid(false);
        }else{
            setFormValid(true);
        }
    }, [taskError]);

    const taskHandler = (e) => {
        setTask(e.target.value);

        if(e.target.value.length < 1 || e.target.value.length > 30){
            setTaskError("Incorrect task");
        }else {
            setTaskError("");
        }
        console.log(taskError);
    }

    const blurHandler = (e) => {
        setTaskDirty(true);
    }

    const addTask = () =>{
        if(formValid){
            setItems(task);
        }
    }

    return (
        <div>
            <form>
                {(taskDirty && taskError) && <div style={{color: "red"}}>{taskError}</div>}
                <h1>Write your task</h1>
                <input value={task} type="text" onChange={e => taskHandler(e)} onBlur={e => blurHandler(e)}/>
                <button disabled={!formValid} onClick={addTask}>Add</button>
            </form>
        </div>
    );
};

export default ToDoForm;
