import React, { useState } from 'react';

const ColorPicker = () => {
    const [colorA, setColorA] = useState(0);
    const [colorB, setColorB] = useState(0);
    const [colorC, setColorC] = useState(0);

    return (
        <div>
            <div
                style={{
                    width: '300px',
                    height: '300px',
                    backgroundColor: `rgb(${colorA}, ${colorB}, ${colorC})`,
                }}
            ></div>
            <input
                min={0}
                max={255}
                type="range"
                onChange={(event) => setColorA(event.target.value)}
            />
            <input
                min={0}
                max={255}
                type="range"
                onChange={(event) => setColorB(event.target.value)}
            />
            <input
                min={0}
                max={255}
                type="range"
                onChange={(event) => setColorC(event.target.value)}
            />
        </div>
    );
};

export default ColorPicker;
