import React, { useState } from 'react';
import './ColorPickerStyle.css';

const generateRandomColor = () => {
    const red = Math.floor(Math.random() * 256);
    const green = Math.floor(Math.random() * 256);
    const blue = Math.floor(Math.random() * 256);
    return [red, green, blue];
};

const generateRGBColorsArray = (length) => {
    const colors = [];
    for (let i = 0; i < length; i++) {
        const color = generateRandomColor();
        colors.push(color);
    }
    return colors;
};

const rgbColorsArray = generateRGBColorsArray(100);
const ColorPicker = () => {
    const [color, setColor] = useState({a: 0, b: 0, c: 0});

    const handleClick = (rgbColor) => {
        setColor({a: rgbColor[0], b: rgbColor[1], c: rgbColor[2]});
    }

    return (
        <div>
            <div
                style={{
                    width: '300px',
                    height: '300px',
                    backgroundColor: `rgb(${color.a}, ${color.b}, ${color.c})`,
                }}
            ></div>
            <div className="palette">
                {rgbColorsArray.map((rgbColor) => (
                    <button
                        style={{
                            width: '60px',
                            height: '60px',
                            backgroundColor: `rgb(${rgbColor[0]}, ${rgbColor[1]}, ${rgbColor[2]})`,
                        }}
                        onClick={e => handleClick(rgbColor)}
                    ></button>
                ))}
            </div>
        </div>
    );
};

export default ColorPicker;
