import React, { useState } from 'react';

function ColorPicker() {
    const [selectedColor, setSelectedColor] = useState('#ffffff');
    const [savedColors, setSavedColors] = useState([]);

    const handleColorChange = (color) => {
        setSelectedColor(color);
    };

    const handleResetColor = () => {
        setSelectedColor('#ffffff');
    };

    const handleSaveColor = () => {
        setSavedColors([...savedColors, selectedColor]);
    };

    return (
        <div>
            <div className="color-picker">
                <div className="color-picker__palette">
                    <div
                        className="color-picker__color"
                        style={{ backgroundColor: '#ff0000' }}
                        onClick={() => handleColorChange('#ff0000')}
                    ></div>
                    <div
                        className="color-picker__color"
                        style={{ backgroundColor: '#00ff00' }}
                        onClick={() => handleColorChange('#00ff00')}
                    ></div>
                    <div
                        className="color-picker__color"
                        style={{ backgroundColor: '#0000ff' }}
                        onClick={() => handleColorChange('#0000ff')}
                    ></div>
                    {/* Добавьте другие цвета по вашему выбору */}
                </div>
                <div className="color-picker__selected-color">
                    <div
                        className="color-picker__selected-color-preview"
                        style={{ backgroundColor: selectedColor }}
                    ></div>
                    Selected Color
                </div>
                <div>
                    <button onClick={handleResetColor}>Reset</button>
                    <button onClick={handleSaveColor}>Save</button>
                </div>
            </div>

            <div>
                <h3>Saved Colors:</h3>
                {savedColors.map((color, index) => (
                    <div
                        key={index}
                        className="color-picker__saved-color"
                        style={{ backgroundColor: color }}
                    ></div>
                ))}
            </div>
        </div>
    );
}

export default ColorPicker;
