import React, {useEffect, useState} from 'react';

const Search = ({ProductCatalog, setSearchProducs}) => {
    const [searchTerm, setSearchTerm] = useState('');
    const [searchType, setSearchType] = useState('exact');
    const [searchResults, setSearchResults] = useState([]);

    const handleSearchTermChange = (event) => {
        setSearchTerm(event.target.value);
    };

    const handleSearchTypeChange = (event) => {
        setSearchType(event.target.value);
    };

    useEffect(() => {
        let results = [];

        if (searchType === 'exact') {
            results = ProductCatalog.filter((product) =>
                product.Name.toLowerCase() === searchTerm.toLowerCase()
            );
        } else if (searchType === 'partial') {
            results = ProductCatalog.filter((product) =>
                product.Name.toLowerCase().includes(searchTerm.toLowerCase())
            );
        }

        setSearchProducs(results);
    }, [searchTerm, searchType, ProductCatalog]);

    return (
        <div>
            <input
                type="text"
                value={searchTerm}
                onChange={handleSearchTermChange}
                placeholder="Enter search term"
            />
            <select value={searchType} onChange={handleSearchTypeChange}>
                <option value="exact">Exact Match</option>
                <option value="partial">Partial Match</option>
            </select>
        </div>
    );
};

export default Search;
